import React, {useState} from "react";

function CreateArea() {
  const [noteArea, setNoteArea] = useState({
    title: "",
    content: ""
  }); 


  return (
    <div>
      <form>
        <input name="title" value = {noteArea.title} placeholder="Title" />
        <textarea name="content" value = {noteArea.content} placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
