import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {

  const [notes, setNotes] = useState([]); 

  function addNote(note) {
    setNotes(preNotes => {
      return [...preNotes, note];
    })

  }

  function deleteNote(id) {
    setNotes(preNotes => {
      preNotes.filter((noteItem, index) => {
        return index !== id; 
      })
    })



  }
  return (
    <div>
      <Header />
      <CreateArea 
        onAdd = {addNote}
      />
      {notes.map(noteItem => {
        return <Note title = {noteItem.title} content = {noteItem.content} noteDelete = {deleteNote}/>
      })}
      <Footer />
    </div>
  );
}

export default App;
